// Temporarily using dummy data for testing
import { userAPI as dummyUserAPI, restaurantAPI as dummyRestaurantAPI, menuAPI as dummyMenuAPI, orderAPI as dummyOrderAPI } from './dummyApi';

// For testing purposes, export dummy APIs
export const userAPI = dummyUserAPI;

// Restaurant APIs
export const restaurantAPI = dummyRestaurantAPI;

// Menu APIs
export const menuAPI = dummyMenuAPI;

// Order APIs
export const orderAPI = {
  getAllOrders: () => api.get('/orders'),
  createOrder: (orderData) => api.post('/orders', orderData),
  getOrderById: (id) => api.get(`/orders/${id}`),
  getOrdersByCustomer: (customerId) => api.get(`/orders/customer/${customerId}`),
  getOrdersByRestaurant: (restaurantId) => api.get(`/orders/restaurant/${restaurantId}`),
  updateOrderStatus: (id, status) => api.patch(`/orders/${id}/status`, { status }),
  getOrderStats: () => api.get('/orders/stats/overview'),
};

export default api;
